contracts/ContractFactory.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "hardhat/console.sol";

/**
 * Contract Factory that creates individual user contracts for mental health AI assistant
 * Uses minimal proxy pattern for gas efficiency
 * @author BuidlGuidl
 */
contract ContractFactory is Ownable {
    // Core addresses
    address public subscriptionContract;
    address public userContractTemplate;

    // User contract registry
    mapping(address => address) public userContracts; // user => contract
    mapping(address => address) public contractOwners; // contract => user
    mapping(address => bool) public isUserContract; // contract => isUserContract

    // Statistics
    uint256 public totalUserContracts;
    uint256 public factoryVersion = 1;

    // Events
    event UserContractCreated(address indexed user, address indexed userContract, uint256 subscriptionId, uint256 timestamp);
    event SubscriptionContractUpdated(address indexed oldContract, address indexed newContract);
    event UserContractTemplateUpdated(address indexed oldTemplate, address indexed newTemplate);
    event FactoryVersionUpdated(uint256 oldVersion, uint256 newVersion);

    // Errors
    error UserAlreadyHasContract();
    error InvalidUserAddress();
    error InvalidSubscriptionContract();
    error InvalidTemplateAddress();
    error ContractCreationFailed();
    error UnauthorizedCaller();

    // Modifiers
    modifier onlySubscriptionContract() {
        if (msg.sender != subscriptionContract) {
            revert UnauthorizedCaller();
        }
        _;
    }

    modifier validAddress(address _address) {
        if (_address == address(0)) {
            revert InvalidUserAddress();
        }
        _;
    }

    /**
     * Constructor
     * @param _owner Owner of the factory contract
     * @param _userContractTemplate Address of the user contract template
     */
    constructor(address _owner, address _userContractTemplate) Ownable(_owner) {
        if (_userContractTemplate == address(0)) {
            revert InvalidTemplateAddress();
        }
        userContractTemplate = _userContractTemplate;
        console.log("ContractFactory deployed by:", _owner);
        console.log("User contract template:", _userContractTemplate);
    }

    /**
     * Create a new user contract for a user
     * Only callable by subscription contract
     * @param user Address of the user
     * @param subscriptionId Subscription ID from subscription contract
     * @return Address of the created user contract
     */
    function createUserContract(address user, uint256 subscriptionId) 
        external 
        onlySubscriptionContract 
        validAddress(user) 
        returns (address) 
    {
        // Check if user already has a contract
        address existingContract = userContracts[user];
        if (existingContract != address(0)) {
            console.log("User already has contract, returning existing:", existingContract);
            return existingContract; // Return existing contract instead of reverting
        }

        // Clone the user contract template
        address userContract = Clones.clone(userContractTemplate);

        // Validate contract creation
        if (userContract == address(0)) {
            revert ContractCreationFailed();
        }

        // Initialize the user contract
        UserContract(userContract).initialize(user, subscriptionId);

        // Update registry
        userContracts[user] = userContract;
        contractOwners[userContract] = user;
        isUserContract[userContract] = true;
        totalUserContracts++;

        console.log("Created NEW user contract for:", user);
        console.log("Contract address:", userContract);
        console.log("Subscription ID:", subscriptionId);

        emit UserContractCreated(user, userContract, subscriptionId, block.timestamp);

        return userContract;
    }

    /**
     * Get user contract address
     * @param user Address of the user
     * @return Address of the user's contract
     */
    function getUserContract(address user) external view returns (address) {
        return userContracts[user];
    }

    /**
     * Check if address is a user contract
     * @param contractAddress Address to check
     * @return True if it's a user contract
     */
    function checkIsUserContract(address contractAddress) external view returns (bool) {
        return isUserContract[contractAddress];
    }

    /**
     * Get total number of user contracts created
     * @return Total number of user contracts
     */
    function getTotalUserContracts() external view returns (uint256) {
        return totalUserContracts;
    }

    /**
     * Set subscription contract address
     * Only callable by owner
     * @param _subscriptionContract Address of the subscription contract
     */
    function setSubscriptionContract(address _subscriptionContract) external onlyOwner validAddress(_subscriptionContract) {
        address oldContract = subscriptionContract;
        subscriptionContract = _subscriptionContract;

        console.log("Updated subscription contract from:", oldContract, "to:", _subscriptionContract);
        emit SubscriptionContractUpdated(oldContract, _subscriptionContract);
    }

    /**
     * Set user contract template address
     * Only callable by owner
     * @param _template Address of the user contract template
     */
    function setUserContractTemplate(address _template) external onlyOwner validAddress(_template) {
        address oldTemplate = userContractTemplate;
        userContractTemplate = _template;

        console.log("Updated user contract template from:", oldTemplate, "to:", _template);
        emit UserContractTemplateUpdated(oldTemplate, _template);
    }

    /**
     * Upgrade factory version
     * Only callable by owner
     * @param _newVersion New factory version
     */
    function upgradeFactoryVersion(uint256 _newVersion) external onlyOwner {
        uint256 oldVersion = factoryVersion;
        factoryVersion = _newVersion;

        console.log("Upgraded factory version from:", oldVersion, "to:", _newVersion);
        emit FactoryVersionUpdated(oldVersion, _newVersion);
    }

    /**
     * Get factory information
     * @return _subscriptionContract Subscription contract address
     * @return _userContractTemplate User contract template address
     * @return _totalUserContracts Total number of user contracts
     * @return _factoryVersion Factory version
     */
    function getFactoryInfo() external view returns (
        address _subscriptionContract,
        address _userContractTemplate,
        uint256 _totalUserContracts,
        uint256 _factoryVersion
    ) {
        return (subscriptionContract, userContractTemplate, totalUserContracts, factoryVersion);
    }
}

/**
 * Interface for UserContract to enable initialization
 */
interface UserContract {
    function initialize(address user, uint256 subscriptionId) external;
}

contracts/OwnerMessage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {SiweAuth} from "@oasisprotocol/sapphire-contracts/contracts/auth/SiweAuth.sol";
import "hardhat/console.sol";

contract OwnerMessage is SiweAuth {
    address private _owner;
    string private _message;

    modifier onlyOwner(bytes memory token) {
        if (msg.sender != _owner && authMsgSender(token) != _owner) {
            revert("Not the owner");
        }
        _;
    }

    constructor(string memory initialMessage, string memory domain, address customOwner) SiweAuth(domain) {
        _owner = customOwner != address(0) ? customOwner : msg.sender;
        _message = initialMessage;
        console.log("OwnerMessage deployed by:", msg.sender);
        console.log("Contract owner set to:", _owner);
        console.log("Initial message:", initialMessage);
        console.log("Domain:", domain);
    }

    function getMessage(bytes memory token) external view onlyOwner(token) returns (string memory) {
        console.log("getMessage called by:", msg.sender);
        console.log("Token length:", token.length);
        return _message;
    }

    function setMessage(string calldata newMessage, bytes memory token) external onlyOwner(token) {
        console.log("setMessage called by:", msg.sender);
        console.log("New message:", newMessage);
        _message = newMessage;
    }

    function owner() external view returns (address) {
        return _owner;
    }
}

contracts/SubscriptionContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// Useful for debugging. Remove when deploying to a live network.
import "hardhat/console.sol";

/**
 * Interface for ContractFactory
 */
interface ContractFactory {
    function createUserContract(address user, uint256 subscriptionId) external returns (address);
}

/**
 * A smart contract that handles subscription payments in ROSE tokens
 * Users pay 1 ROSE token to subscribe to the service
 * @author BuidlGuidl
 */
contract SubscriptionContract {
    // State Variables
    address public immutable owner;
    uint256 public subscriptionPrice = 1 ether; // 1 ROSE token
    uint256 public totalSubscribers = 0;
    address public contractFactory; // Contract factory address

    // Subscription tracking
    mapping(address => bool) public subscribers;
    mapping(address => uint256) public subscriptionTimestamp;
    mapping(address => uint256) public subscriptionCount;

    // Events
    event SubscriptionPurchased(address indexed subscriber, uint256 amount, uint256 timestamp);
    event SubscriptionCancelled(address indexed subscriber, uint256 timestamp);
    event PriceUpdated(uint256 newPrice, uint256 timestamp);
    event FundsWithdrawn(address indexed owner, uint256 amount, uint256 timestamp);
    event ContractFactoryUpdated(address indexed oldFactory, address indexed newFactory);
    event UserContractCreated(address indexed subscriber, address indexed userContract, uint256 timestamp);

    // Constructor
    constructor(address _owner) {
        owner = _owner;
        console.log("SubscriptionContract deployed by:", _owner);
    }

    // Modifier
    modifier isOwner() {
        require(msg.sender == owner, "Not the Owner");
        _;
    }



    modifier isSubscribed() {
        require(subscribers[msg.sender], "Not subscribed");
        _;
    }

    /**
     * Function to subscribe to the service
     * Users can subscribe multiple times to extend their subscription
     * Requires exactly 1 ROSE token payment per subscription
     */
    function subscribe() public payable {
        require(msg.value >= subscriptionPrice, "Insufficient payment: 1 ROSE token required");

        console.log("New subscription from:", msg.sender, "Amount:", msg.value);

        // Track subscription - first time subscribers increment total count
        if (!subscribers[msg.sender]) {
            totalSubscribers += 1;
        }

        // Mark user as subscribed and update timestamp
        subscribers[msg.sender] = true;
        subscriptionTimestamp[msg.sender] = block.timestamp;
        subscriptionCount[msg.sender] += 1;

        // Create user contract if factory is set
        if (contractFactory != address(0)) {
            try ContractFactory(contractFactory).createUserContract(msg.sender, subscriptionCount[msg.sender]) returns (address userContract) {
                console.log("Created user contract for:", msg.sender, "at:", userContract);
                emit UserContractCreated(msg.sender, userContract, block.timestamp);
            } catch {
                console.log("Failed to create user contract for:", msg.sender);
            }
        }

        // Refund excess payment if any
        if (msg.value > subscriptionPrice) {
            uint256 refund = msg.value - subscriptionPrice;
            (bool refundSuccess, ) = msg.sender.call{value: refund}("");
            require(refundSuccess, "Refund failed");
        }

        emit SubscriptionPurchased(msg.sender, subscriptionPrice, block.timestamp);
    }

    /**
     * Function to cancel subscription
     * No refund - subscription remains valid until cancellation
     */
    function cancelSubscription() public isSubscribed {
        subscribers[msg.sender] = false;
        totalSubscribers -= 1;

        emit SubscriptionCancelled(msg.sender, block.timestamp);
    }

    /**
     * Check if an address is subscribed
     */
    function isUserSubscribed(address _user) public view returns (bool) {
        return subscribers[_user];
    }

    /**
     * Get subscription timestamp for a user
     */
    function getSubscriptionTime(address _user) public view returns (uint256) {
        require(subscribers[_user], "User not subscribed");
        return subscriptionTimestamp[_user];
    }

    /**
     * Get subscription count for a user
     */
    function getSubscriptionCount(address _user) public view returns (uint256) {
        return subscriptionCount[_user];
    }

    /**
     * Owner function to update subscription price
     */
    function setSubscriptionPrice(uint256 _newPrice) public isOwner {
        require(_newPrice > 0, "Price must be greater than 0");
        subscriptionPrice = _newPrice;
        emit PriceUpdated(_newPrice, block.timestamp);
    }

    /**
     * Owner function to withdraw collected subscription fees
     */
    function withdraw() public isOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");

        (bool success, ) = owner.call{value: balance}("");
        require(success, "Withdrawal failed");

        emit FundsWithdrawn(owner, balance, block.timestamp);
    }

    /**
     * Get contract balance
     */
    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    /**
     * Set contract factory address
     * Only callable by owner
     */
    function setContractFactory(address _contractFactory) public isOwner {
        require(_contractFactory != address(0), "Invalid factory address");
        address oldFactory = contractFactory;
        contractFactory = _contractFactory;

        console.log("Updated contract factory from:", oldFactory, "to:", _contractFactory);
        emit ContractFactoryUpdated(oldFactory, _contractFactory);
    }

    /**
     * Function that allows the contract to receive ROSE tokens
     */
    receive() external payable {
        console.log("Received ROSE tokens:", msg.value, "from:", msg.sender);
    }
}

contracts/UserContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import {SiweAuth} from "@oasisprotocol/sapphire-contracts/contracts/auth/SiweAuth.sol";
import "hardhat/console.sol";

/**
 * User Contract for storing encrypted chat data
 * Each user gets their own contract for privacy
 * @author BuidlGuidl
 */
contract UserContract is Ownable, SiweAuth {
    constructor(string memory domain) Ownable(msg.sender) SiweAuth(domain) {}
    // User data
    address public user;
    uint256 public subscriptionId;
    uint256 public createdAt;
    bool public isInitialized;

    // Chat session management
    struct ChatSession {
        bytes32 sessionId;
        uint256 createdAt;
        uint256 lastActivity;
        bool isActive;
        uint256 messageCount;
    }

    // Chat message structure
    struct ChatMessage {
        bytes encryptedUserMessage;
        bytes encryptedAIResponse;
        uint256 timestamp;
        uint256 messageOrder;
    }

    // Chat summary structure
    struct ChatSummary {
        bytes32 sessionId;
        bytes encryptedSummary;
        uint256 createdAt;
        uint256 messageCount;
    }

    // Storage mappings
    mapping(bytes32 => ChatSession) public chatSessions;
    mapping(bytes32 => ChatMessage[]) public chatMessages;
    mapping(bytes32 => ChatSummary) public chatSummaries;
    mapping(bytes32 => uint256) public sessionMessageCount;
    bytes32[] public userSessionIds;

    // TEE contract access (for AI processing)
    address public teeContract;

    // Events
    event ChatSessionCreated(address indexed user, bytes32 indexed sessionId, uint256 timestamp);
    event ChatMessageStored(address indexed user, bytes32 indexed sessionId, uint256 messageOrder, uint256 timestamp);
    event ChatSessionClosed(address indexed user, bytes32 indexed sessionId, uint256 timestamp);
    event ChatSummaryStored(address indexed user, bytes32 indexed sessionId, uint256 timestamp);
    event TEEContractUpdated(address indexed oldTEE, address indexed newTEE);
    event UserContractInitialized(address indexed user, uint256 subscriptionId, uint256 timestamp);

    // Errors
    error ContractNotInitialized();
    error SessionNotFound();
    error SessionAlreadyExists();
    error UnauthorizedAccess();
    error InvalidSessionId();
    error InvalidMessageOrder();

    // Modifiers
    modifier onlyInitialized() {
        if (!isInitialized) {
            revert ContractNotInitialized();
        }
        _;
    }

    modifier onlyUser(bytes memory token) {
        if (msg.sender != user && authMsgSender(token) != user) {
            revert UnauthorizedAccess();
        }
        _;
    }

    modifier onlyUserDirect() {
        if (msg.sender != user) {
            revert UnauthorizedAccess();
        }
        _;
    }

    modifier onlyTEE() {
        if (msg.sender != teeContract) {
            revert UnauthorizedAccess();
        }
        _;
    }

    modifier validSessionId(bytes32 sessionId) {
        if (sessionId == bytes32(0)) {
            revert InvalidSessionId();
        }
        _;
    }

    /**
     * Initialize the user contract
     * Can only be called once by the factory
     */
    function initialize(address _user, uint256 _subscriptionId) external {
        require(!isInitialized, "Already initialized");
        require(_user != address(0), "Invalid user address");

        user = _user;
        subscriptionId = _subscriptionId;
        createdAt = block.timestamp;
        isInitialized = true;

        emit UserContractInitialized(_user, _subscriptionId, block.timestamp);
    }

    /**
     * Create a new chat session
     * @return sessionId The new session ID
     */
    function createNewSession() external onlyUserDirect onlyInitialized returns (bytes32) {
        uint256 sessionCounter = userSessionIds.length;
        bytes32 sessionId = keccak256(abi.encodePacked(user, block.timestamp, sessionCounter));

        // Ensure unique session ID
        while (chatSessions[sessionId].createdAt != 0) {
            sessionCounter++;
            sessionId = keccak256(abi.encodePacked(user, block.timestamp, sessionCounter));
        }

        ChatSession memory newSession = ChatSession({
            sessionId: sessionId,
            createdAt: block.timestamp,
            lastActivity: block.timestamp,
            isActive: true,
            messageCount: 0
        });

        chatSessions[sessionId] = newSession;
        userSessionIds.push(sessionId);
        sessionMessageCount[sessionId] = 0;

        emit ChatSessionCreated(user, sessionId, block.timestamp);

        return sessionId;
    }

    /**
     * Store a chat message in a session
     * @param sessionId The session ID
     * @param encryptedUserMessage Encrypted user message
     * @param encryptedAIResponse Encrypted AI response
     */
    function storeChatMessage(
        bytes32 sessionId,
        bytes calldata encryptedUserMessage,
        bytes calldata encryptedAIResponse
    ) external onlyUserDirect onlyInitialized validSessionId(sessionId) {
        require(chatSessions[sessionId].createdAt != 0, "Session not found");
        require(chatSessions[sessionId].isActive, "Session is closed");

        uint256 messageOrder = sessionMessageCount[sessionId];

        ChatMessage memory newMessage = ChatMessage({
            encryptedUserMessage: encryptedUserMessage,
            encryptedAIResponse: encryptedAIResponse,
            timestamp: block.timestamp,
            messageOrder: messageOrder
        });

        chatMessages[sessionId].push(newMessage);
        sessionMessageCount[sessionId]++;
        chatSessions[sessionId].lastActivity = block.timestamp;
        chatSessions[sessionId].messageCount++;

        emit ChatMessageStored(user, sessionId, messageOrder, block.timestamp);
    }

    /**
     * Store a chat summary (called by user with SIWE authentication)
     * @param sessionId The session ID
     * @param encryptedSummary Encrypted summary from AI
     * @param token SIWE authentication token
     */
    function storeChatSummary(
        bytes32 sessionId,
        bytes calldata encryptedSummary,
        bytes memory token
    ) external onlyUser(token) onlyInitialized validSessionId(sessionId) {
        require(chatSessions[sessionId].createdAt != 0, "Session not found");

        ChatSummary memory summary = ChatSummary({
            sessionId: sessionId,
            encryptedSummary: encryptedSummary,
            createdAt: block.timestamp,
            messageCount: chatSessions[sessionId].messageCount
        });

        chatSummaries[sessionId] = summary;

        emit ChatSummaryStored(user, sessionId, block.timestamp);
    }

    /**
     * Get chat history for a session
     * @param sessionId The session ID
     * @return Array of chat messages
     */
    function getChatHistory(bytes32 sessionId) external view onlyUserDirect onlyInitialized validSessionId(sessionId) returns (ChatMessage[] memory) {
        require(chatSessions[sessionId].createdAt != 0, "Session not found");
        return chatMessages[sessionId];
    }

    /**
     * Get chat summary for a session
     * @param sessionId The session ID
     * @return Chat summary
     */
    function getChatSummary(bytes32 sessionId, bytes memory token) external view onlyUser(token) onlyInitialized validSessionId(sessionId) returns (ChatSummary memory) {
        require(chatSessions[sessionId].createdAt != 0, "Session not found");
        return chatSummaries[sessionId];
    }

    /**
     * Get session information
     * @param sessionId The session ID
     * @return Session information
     */
    function getSessionInfo(bytes32 sessionId) external view onlyUserDirect onlyInitialized validSessionId(sessionId) returns (ChatSession memory) {
        require(chatSessions[sessionId].createdAt != 0, "Session not found");
        return chatSessions[sessionId];
    }

    /**
     * Get all active sessions for the user
     * @return Array of active session IDs
     */
    function getActiveSessions() external view onlyUserDirect onlyInitialized returns (bytes32[] memory) {
        bytes32[] memory activeSessions = new bytes32[](userSessionIds.length);
        uint256 activeCount = 0;

        for (uint256 i = 0; i < userSessionIds.length; i++) {
            bytes32 sessionId = userSessionIds[i];
            if (chatSessions[sessionId].isActive) {
                activeSessions[activeCount] = sessionId;
                activeCount++;
            }
        }

        // Resize array to actual count
        bytes32[] memory result = new bytes32[](activeCount);
        for (uint256 i = 0; i < activeCount; i++) {
            result[i] = activeSessions[i];
        }

        return result;
    }

    /**
     * Close a chat session
     * @param sessionId The session ID to close
     */
    function closeSession(bytes32 sessionId) external onlyUserDirect onlyInitialized validSessionId(sessionId) {
        require(chatSessions[sessionId].createdAt != 0, "Session not found");
        require(chatSessions[sessionId].isActive, "Session already closed");

        chatSessions[sessionId].isActive = false;
        chatSessions[sessionId].lastActivity = block.timestamp;

        emit ChatSessionClosed(user, sessionId, block.timestamp);
    }

    /**
     * Delete a session and all its data
     * @param sessionId The session ID to delete
     */
    function deleteSession(bytes32 sessionId) external onlyUserDirect onlyInitialized validSessionId(sessionId) {
        require(chatSessions[sessionId].createdAt != 0, "Session not found");

        // Remove from userSessionIds array
        for (uint256 i = 0; i < userSessionIds.length; i++) {
            if (userSessionIds[i] == sessionId) {
                userSessionIds[i] = userSessionIds[userSessionIds.length - 1];
                userSessionIds.pop();
                break;
            }
        }

        // Clear session data
        delete chatSessions[sessionId];
        delete chatMessages[sessionId];
        delete chatSummaries[sessionId];
        delete sessionMessageCount[sessionId];
    }

    /**
     * Set TEE contract address (for AI processing)
     * @param _teeContract TEE contract address
     */
    function setTEEContract(address _teeContract) external onlyOwner {
        require(_teeContract != address(0), "Invalid TEE contract address");
        address oldTEE = teeContract;
        teeContract = _teeContract;

        emit TEEContractUpdated(oldTEE, _teeContract);
    }

    /**
     * Get user contract information
     * @return _user User address
     * @return _subscriptionId Subscription ID
     * @return _createdAt Contract creation timestamp
     * @return _totalSessions Total number of sessions
     */
    function getUserInfo() external view onlyUserDirect onlyInitialized returns (
        address _user,
        uint256 _subscriptionId,
        uint256 _createdAt,
        uint256 _totalSessions
    ) {
        return (user, subscriptionId, createdAt, userSessionIds.length);
    }

    /**
     * Get total message count for a session
     * @param sessionId The session ID
     * @return Message count
     */
    function getSessionMessageCount(bytes32 sessionId) external view onlyUserDirect onlyInitialized validSessionId(sessionId) returns (uint256) {
        return sessionMessageCount[sessionId];
    }

    /**
     * Get all session IDs for the user
     * @return Array of session IDs
     */
    function getAllSessionIds(bytes memory token) external view onlyUser(token) onlyInitialized returns (bytes32[] memory) {
        return userSessionIds;
    }
}

